import { json } from "react-router-dom";
export default function getAuthToken() {
  const token = localStorage.getItem("token");
  const tokenDuration = tokenEnd();
  if (tokenDuration < 0) {
    return 'EXPIRED';
  }
  return token;
}

export function getTokenDuration() {
  const storedTokenEndTime = localStorage.getItem("tokenEndTime");
  const expirationDate = new Date(storedTokenEndTime);
  const now = new Date();
  const duration = now.getTime() - expirationDate.getTime();
  return duration;
}
export function getToken() {
  return getAuthToken();
}

export function checkAuthToken() {
  const token = localStorage.getItem("token");
  if (!token) throw json({ message: "Invalid Page" }, { status: 404 });
  return null;
}
