import { useState, useRef } from "react";
import { Form } from "react-router-dom";

import classes from "./AuthForm.module.css";

function AuthForm() {
  const [isLogin, setIsLogin] = useState(true);
  const [isMatch, setIsMatch] = useState(false);
  const [isTouch, setIsTouch] = useState(false);
  const password = useRef();
  const confirmPassword = useRef();

  function switchAuthHandler() {
    setIsLogin((isCurrentlyLogin) => !isCurrentlyLogin);
  }

  const passwordHandler = () => {
    if (!isLogin) {
      if (password.current.value === confirmPassword.current.value) {
        setIsMatch(true);
      } else setIsMatch(false);
    }
  };

  const onConfirm = () => {
    setIsTouch(true);
    if (password.current.value === confirmPassword.current.value) {
      setIsMatch(true);
    } else setIsMatch(false);
  };

  const showPassword = (input) => {
    const passwordType =
      password.current.type === "password" ? "text" : "password";
    if (!isLogin) {
      password.current.type = passwordType;
      confirmPassword.current.type = passwordType;
    } else {
      password.current.type = passwordType;
    }
  };

  return (
    <>
      <Form method="post" className={classes.form}>
        <h1>{isLogin ? "Log in" : "Create a new user"}</h1>
        <p>
          <label htmlFor="email">Email</label>
          <input id="email" type="email" name="email" required />
        </p>
        <p>
          <label htmlFor="image">Password</label>
          <input
            ref={password}
            id="password"
            type="password"
            name="password"
            onInput={passwordHandler}
            required
          />
          <img
            src={process.env.PUBLIC_URL + "img/eye.png"}
            alt="eye"
            onClick={showPassword}
            width="24px"
          />
        </p>
        {!isLogin && (
          <>
            <p>
              <label htmlFor="image">Confirm Password</label>
              <input
                ref={confirmPassword}
                id="confirmpassword"
                type="password"
                name="password"
                onInput={onConfirm}
                required
              />
              <img
                src={process.env.PUBLIC_URL + "img/eye.png"}
                alt="eye"
                onClick={showPassword}
                width="24px"
              />
            </p>
            {!isMatch && isTouch && (
              <p style={{ color: "#E91E63" }}>The Password Doesn't Match!</p>
            )}
          </>
        )}

        <div className={classes.actions}>
          <button onClick={switchAuthHandler} type="button">
            {isLogin ? "Create new user" : "Login"}
          </button>
          <button>{isLogin ? "Login" : "Save"}</button>
        </div>
      </Form>
    </>
  );
}

export default AuthForm;
