import { json } from "react-router-dom";
export default function getAuthToken() {
  const token = localStorage.getItem("token");
  return token;
}

export function tokenEnd() {
  const storedTokenEnd = localStorage.getItem("tokenEndTime");
  const expirationDate = new Date(storedTokenEnd);
  const now = new Date();
  const a = now.getTime() - expirationDate.getTime();
  return a
}
export function getToken() {
  return getAuthToken();
}

export function checkAuthToken() {
  const token = localStorage.getItem("token");
  if (!token) throw json({ message: "Invalid Page" }, { status: 404 });
  return null
}
