import {json, redirect} from 'react-router-dom'
export default function getAuthToken() {
  const token = localStorage.getItem("token");
  return token;
}

export function getToken() {
  return getAuthToken()
}

export function checkAuthToken() {
  const token = localStorage.getItem("token") 
  if(!token) return redirect('/')
  return null
}
